// import { Button } from './elements/Button';

import Button from './elements/Button';

export const Banner = () => {
  return (
    <div className="banner w-full md:w-2/3 px-7 mx-auto relative flex items-center justify-between">
      <div className="banner-description w-full md:w-1/2 p-3">
        <h2 className="mb-6 text-4xl font-bold text-white">
          Food Ordering Made Easy
        </h2>
        <p className="font-semibold text-lg text-red-600 py-2">
          Get Started Today!
        </p>
        <div className="btn-container">
          <Button>Order Now</Button>
          <a
            href="/menu"
            className="text-yellow-400 hover:text-yellow-500 font-bold text-decoration-line px-3"
          >
            See Menu
          </a>
        </div>
      </div>
      <div className="banner-image w-full md:w-1/2 p-3 flex justify-end">
        <img src={require("../assets/images/pizza_banner.png")} alt="banner" className="max-h-96" />
      </div>
      <div className="absolute -bottom-20 -right-32 rounded-full w-64 h-64 blur-2xl bg-gradient-to-t from-rose-500 to-purple-500"></div>
    </div>
  );
};
